<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class Country_model extends CI_Model {	 	function __construct()    {        parent::__construct();		$this->load->database();    }		function GetList(){				$sqlQuery = "select * from country order by name ASC";		$resultObj = $this->db->query($sqlQuery);				return $resultObj->result_array();			}			}