<?php if ( ! defined('BASEPATH')) exit('No direct script access allowed');class User_profile_model extends CI_Model {	 	function __construct()    {        parent::__construct();		$this->load->database();    }	function Add($userData){				$sqlQuery = "select * from user_profile where email_address = '{$userData['email_address']}'";		$resultObj = $this->db->query($sqlQuery);				if($resultObj->num_rows() > 0){			return -1;		}				return $this->db->insert('user_profile', $userData); 	}		function Update($user_id, $userData){				$this->db->where('user_id', $user_id);		return $this->db->update('user_profile', $userData); 	}		function GetDetailsByEmail($email){				$sqlQuery = "select * from user_profile where email_address = '{$email}'";		$resultObj = $this->db->query($sqlQuery);				return $resultObj->first_row("array"); 	}		function GetDetails($user_id){				$sqlQuery = "select * from user_profile where user_id = '{$user_id}'";		$resultObj = $this->db->query($sqlQuery);				return $resultObj->first_row("array");			}	}